const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT';

let initialState = {
        dialogs: [{id: 'david', name: 'David Bowie'},
            {id: 'queen', name: 'Group Queen'},
            {id: 'kenye', name: 'Kanye West'}],
        messages: [{id: 1, text: 'Pressure pushing down on me'},
            {id: 2, text: 'Pressing down on you, no man ask for'},
            {id: 3, text: 'Um ba ba be'}],
        newMessageText: ''
}

const messagesReducer = (state = initialState, action) => {

    let stateCopy;

    switch (action.type) {
        case ADD_MESSAGE: {
            let newMessage = {id: 4, text: state.newMessageText}
            stateCopy = {...state, messages: [...state.messages]};
            stateCopy.messages.push(newMessage);
            stateCopy.newMessageText = '';
            return stateCopy;
        }

        case UPDATE_NEW_MESSAGE_TEXT: {
            stateCopy = {...state};
            stateCopy.newMessageText = action.newText;
            return stateCopy;
        }
        default:
            return state;
    }
}

export const addMessageActionCreator = () => ({type:ADD_MESSAGE});

export const updateNewMessageTextActionText = (text) =>
    ({type:UPDATE_NEW_MESSAGE_TEXT, newText: text});

export default messagesReducer;
