const FOLLOWING = 'FOLLOWING';
const REMOVE = 'REMOVE';
const SET_USERS = 'SET_USERS';

let initialState = {
        users: [
            // { id: 1, photoUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRZ5J4z_a4KoMnaFB478xmoxOc_ghPx4Vs6kh6rz-rDYwl9M-qo0vaGvpZfpiTJb-bt13w&usqp=CAU',
