const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
        users: [
            { id: 1, followed: true, fullName: 'David Bowie', status: 'Life looks like a play field', location: { city: 'London', country: 'US'} },
            { id: 2, followed: false, fullName: 'Queen', status: 'We are the champions', location: { city: 'London', country: 'US'} },
            { id: 3, followed: false, fullName: 'Kanye West', status: 'ye ', location: { city: 'Chicago', country: 'USA'} },
            { id: 4, followed: true, fullName: 'Jared Leto', status: 'Bartholomew Cubbins', location: { city: 'Louisiana', country: 'USA'} },
        ],
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if(u.id === action.userId) {
                        return {...u, followed: true};
                    }
                  return u
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if(u.id === action.userId) {
                        return {...u, followed: false};
                    }
                    return u
                })
            }
        case SET_USERS: {
            return { ...state, users: [ ...state.users, ...action.users] }
        }
        default: return state;
    }
};

export const followAC = (userId) => ({type: FOLLOW, userId});
export const unFollowAC = (userId) => ({type: UNFOLLOW, userId});
export const setUsersAC = (users) => ({type: SET_USERS, users});

export default usersReducer;
