import s from './MyPosts.module.css';
import Post from './Post/Post';
import React from "react";


const MyPosts = (props) => {

    let postsElements = props.posts.map( p => (<Post id={p.id} like={p.likesCount} text={p.text} />) );

    let newPostElement = React.createRef();

    let addPost = () => {
        let text = newPostElement.current.value;
        alert(text);
    }

    return (
    <div>
        <h1> My posts </h1>
        <div className={s.split}>
            <div className={s.field}>
                <textarea placeholder='Напишите новый пост' ref={newPostElement}/>
                <button onClick={addPost}><a href='#'>Send</a></button>
            </div>

            <div className={s.noDiv}></div>
        </div>

        <div className={s.posts}>
            { postsElements }
        </div>
    </div>
    )
}

export default MyPosts;
