import s from './Users.module.css';
import React from "react";
import userPhoto from "../../img/user.png";
import {NavLink} from "react-router-dom";
import {UserFollow} from "../../api/api";




let UsersFunctional = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }
    return <div>
        <div>
            { pages.map( p => {
                return <span className={ (props.currentPage === p && s.selectedPage) || (props.currentPage !== p && s.unselectedPage) }
                             onClick={ () => {props.onPageChange(p)} } >{p}  </span>
            } ) }
        </div>
        {
            props.users.map( u => <div key={u.id}>
                <div className={s.user}>
                    <div className={s.userPhoto}>
                        <NavLink to={'/profile/' + u.id}>
                            <img alt='photo' src={ u.photos.small != null ? u.photos.small : userPhoto }/>
                        </NavLink>
                    </div>
                    <div className={s.userInfo}>
                        <h4 className={s.userFullName}>{u.name}</h4>
                        <h5 className={s.userStatus}>{u.status}</h5>
                        <h5 className={s.userLocation}>{'u.location.city'}, {'u.location.country'}</h5>
                    </div>
                    <div className={s.userFollow}>

                        {u.followed
                            ? <button disabled={props.followingInProgress.some(userId=> userId === u.id)}
                                onClick={() => {
                                    props.toggleInprogress(true, u.id);
                                    UserFollow.removeUser(u.id)
                                        .then(data => {
                                            if (data.resultCode === 0) {
                                                props.following(u.id)
                                            }
                                            props.followingInProgress(false, u.id);

                                        });
                            }}>Remove</button>

                            : <button disabled={props.followingInProgress.some(id => id === u.id)}
                                onClick={() => {
                                    props.followingInProgress(true, u.id);
                                    UserFollow.followUser(u.id)
                                        .then(data => {
                                            if (data.resultCode === 0) {
                                                props.remove(u.id)
                                            }
                                            props.followingInProgress(false, u.id);
                                        });
                                }}>Following</button>
                        }
                    </div>
                </div>
            </div>)
        }
    </div>
}

export default UsersFunctional;
