import axios from "axios";

const getDeleteInstance = axios.create({
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    withCredentials: true,
    headers: {
        'API-KEY': '3667b2d2-cd74-4d0f-8711-6a077428126f'
    }
})

export const getUsers = (currentPage=1, pageSize=10) => {
    return getDeleteInstance.get(`users?page=${currentPage}&count=${pageSize}`)
        .then(response => response.data);
}

export const deleteUser = (id) => {
    return getDeleteInstance.delete(`follow/${id}`)
        .then(response =>
            response.data);
}



const postInstance = axios.create({
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    data: {},
    withCredentials:true,
    headers: {
        'API-KEY': '3667b2d2-cd74-4d0f-8711-6a077428126f'
    }
})

export const postUser = (id) => {
    return postInstance.post(`follow/${id}`)
        .then(response =>
            response.data);
}
