import s from './MyPosts.module.css';
import Post from './Post/Post';
import React from "react";
import { Form, Field } from 'react-final-form';


const  MyPosts = (props) => {

    let onSubmit = (formData) => {
        console.log(formData)
    }
    let onAddPost = () => {
        props.addPost()
    };

    let onPostChange = (e) => {
        let text = e.target.value
        props.updateNewPost(text)
    };
    return (
    <div>
        <h1> My posts </h1>
        <div className={s.split}>
            <div className={s.field}>
                <Form onSubmit={onSubmit}>
                    {({handleSubmit}) => (
                        <form onSubmit={handleSubmit}>
                        <Field type={'textarea'} placeholder='Make your the new post...' value={props.newPostText}/>
                        <button onClick={onAddPost}><a href='#'>Send</a> </button>
                        </form>
                    )}

                </Form>

            </div>

            <div className={s.noDiv}></div>
        </div>

        <div className={s.posts}>
            <div>
                {props.posts.map(p => (
                    <Post id={p.id} like={p.likesCount} key={p.id} text={p.text} profile={props.profile}/>
                ))}
            </div>
        </div>
    </div>
    )
};

export default MyPosts;
