import s from './Messages.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import React from "react";

const Messages = (props) => {

    let dialogsElements = props.dialogs.map( dialog => (<DialogItem name={dialog.name} id={dialog.id}/>) );
    let messagesElements = props.messages.map( message => (<Message message={message.text} />) );

    let onSendMessage = () => {
        props.addMessage();
    };

    let onMessageChange = (e) => {
        let text = e.target.value;
        props.updateNewMessage(text);
    };

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                <div>{dialogsElements}</div>
            </div>

            <div className={s.messages}>
                <div>{messagesElements}</div>
                <textarea  onChange={onMessageChange} placeholder='Send a message...' value={props.newMessageText}></textarea>
                <button onClick={onSendMessage}><a href='#'>Send</a></button>
            </div>
        </div>
    )
}

export default Messages;
