import {connect} from "react-redux";
import {following, remove, setCurrentPage, setTotalUsersCount, setUsers, toggleIsFetching} from "../../redux/usersReducer";
import React from "react";
import axios from "axios";
import UsersFunctional from "./UsersFunctional";
import Preloader from "../common/preloader/preloader";


class UsersClass extends React.Component {
    componentDidMount() {
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?count=${this.props.pageSize}&page=${this.props.currentPage}`, {withCredentials:true})
            .then(response => {
                this.props.toggleIsFetching(false);
                this.props.setUsers(response.data.items);
                // this.props.setTotalUsersCount(response.data.totalCount);
            })
    }

    onPageChange = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?count=${this.props.pageSize}&page=${this.props.currentPage}`, {withCredentials:true})
            .then(response => {
                this.props.toggleIsFetching(false);
                this.props.setUsers(response.data.items)
            })
    }

    render = () => {
        return <>
            { this.props.isFetching ? <Preloader /> : null }
            <UsersFunctional totalUsersCount={this.props.totalUsersCount}
                             pageSize={this.props.pageSize}
                             currentPage={this.props.currentPage}
                             onPageChange={this.onPageChange}
                             users={this.props.users}
                             following={this.props.following}
                             remove={this.props.remove}/>
        </>
    }
}

let mapStateToProps = (state) => {
    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching
    }
}


const UsersContainer = connect (mapStateToProps,
    {following, remove, setUsers, setCurrentPage, setTotalUsersCount, toggleIsFetching})
(UsersClass);

export default UsersContainer;

