import { Form, Field } from 'react-final-form';
import s from "./Login.module.css";

const Login = (props) => {
    return <>
        <h3 className={s.login}>Login</h3>
        <LoginForm />
    </>
}

const LoginForm = (props) => {

    const onSubmit = (formData) => {
        console.log(formData)
    }
    return (
        <Form
            onSubmit={onSubmit}
        >
            {({handleSubmit, pristine, reset, submitting}) => (
                <div className={s.loginPage}>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label>Username</label>
                            <Field name='userName' component='input' placeholder='Username' type='text'/>
                        </div>
                        <div>
                            <label>Password</label>
                            <Field name='password' component='input' placeholder='Password' type='password'/>
                        </div>
                        <div className={s.checkBoxDiv}>
                            <Field name='rememberMe' component='input' type='checkbox'/>
                            <label className={s.rememberMe}>Remember me</label>
                        </div>
                        <div>
                            <button className={s.buttonLogin}><a>Login</a></button>
                        </div>
                    </form>
                </div>
            )
            }
        </Form>
    )
}

const mapStateToProps = (state) => {
    return {
        email: state.auth.email,
        password: state.auth.password,
        rememberMe: state.auth.rememberMe
    }
}


export default Login;
