import s from './MyPosts.module.css';
import Post from './Post/Post';
import React from "react";

const  MyPosts = (props) => {
    let postsElements = props.posts.map( p => (<Post id={p.id} like={p.likesCount} key={p.id} text={p.text} />) );

    let onAddPost = () => {
        props.addPost();
    };

    let onPostChange = (e) => {
        let text = e.target.value;
        props.updateNewPostActionCreator(text);
    };
    return (
    <div>
        <h1> My posts </h1>
        <div className={s.split}>
            <div className={s.field}>
                <textarea onChange={onPostChange} placeholder='Make your the new post...' value={props.newPostText}/>
                <button onClick={onAddPost}><a href='#'>Send</a> </button>
            </div>

            <div className={s.noDiv}></div>
        </div>

        <div className={s.posts}>
            <div>{postsElements}</div>
        </div>
    </div>
    )
};

export default MyPosts;
