import React from "react";
import Profile from "./Profile";
import {connect} from "react-redux";
import {profileUser} from "../../redux/profileReducer";
import {Navigate, useLocation, useNavigate, useParams} from "react-router-dom";
import {withAuthRedirect} from "../../hoc/withAuthRedirect";
import {compose} from "redux";

class  ProfileClass extends React.Component {
    componentDidMount() {
        let userId = this.props.router.params.userId;
        if(!userId){
            userId = 2;
        }
        this.props.profileUser(userId);
    }
    render () {
        return (
            <div>
                <Profile {...this.props} profile={this.props.profile} isAuth={this.props.isAuth}/>
            </div>
        )
    }
}

compose(
    ProfileClass
)(Profile);

let AuthRedirectComponent = withAuthRedirect(ProfileClass);

    function withRouter(Component) {
        function ComponentWithRouterProps(props) {
            let location = useLocation();
            let navigate = useNavigate();
            let params = useParams();
            return (
                <Component {...props} router={{location, navigate, params}}/>
            );
        }

        return ComponentWithRouterProps;
    }
    let mapStateToProps = (state) => {
        return {
            profile: state.profilePage.profile,
        }
    }

const ProfileContainer = connect (mapStateToProps, {profileUser}) (withRouter(AuthRedirectComponent));
export default ProfileContainer;
