import ReactDOM from "react-dom";
import React from "react";
import App from "../../App";

let rerenderEntireTree = (state) => {
    ReactDOM.render(
        <React.StrictMode>
            <App state={state} addPost={addPost} addMessage={addMessage} updateNewPost={updateNewPostText}/>
        </React.StrictMode>,
        document.getElementById('root')
    );
}

let state = {
    profilePage: {
        posts: [
            {id: 'p1', likesCount: 12, text: "Oh, I was not made for heaven. No, I don't want to go to heaven. Hell is much better. Think of all the interesting people you're going to meet down there!"},
            {id: 'p2', likesCount: 3, text: "The reason we're successful, darling? My overall charisma, of course."},
            {id: 'p3', likesCount: 72, text: "I am a romantic, but I do put up a barrier around myself, so it is hard for people to get in and to know the real me."}
        ],
        newPostText: 'Make your the new post...'
    },
    messagesPage: {
        dialogs: [ {id: 'david', name: 'David Bowie'},
            {id: 'queen', name: 'Group Queen'},
            {id: 'kenye', name: 'Kanye West'}],
        messages: [{id: 1, text: 'Pressure pushing down on me'},
            {id: 2, text:'Pressing down on you, no man ask for'},
            {id: 3, text:'Um ba ba be'}]
    }
};
 window.state = state;

export let addPost = () => {
    let newPost = {
        id: 'p4',
        likesCount: 0,
        text: state.profilePage.newPostText
    }
    state.profilePage.posts.unshift(newPost);
    state.profilePage.newPostText = '';
    rerenderEntireTree(state);
}

export let updateNewPostText = (newText) => {
    state.profilePage.newPostText = newText;
    rerenderEntireTree(state);
}

export let addMessage = (messageText) => {
    let newMessage = {
        id: 4,
        text: messageText
    }
    state.messagesPage.messages.push(newMessage);
    rerenderEntireTree(state);
}



export default state;
