import s from './MyPosts.module.css';
import Post from './Post/Post';
import React from "react";
import { Form, Field } from 'react-final-form';


const  MyPosts = (props) => {

    let onAddPost = () => {
        props.addPost()
    };

    let onPostChange = (e) => {
        let text = e.target.value
        props.updateNewPost(text)
    };
    return (
    <div>
        <h1> My posts </h1>
        <div className={s.split}>
            <div className={s.field}>

                <MyPostForm/>

            </div>

            <div className={s.noDiv}></div>
        </div>

        <div className={s.posts}>
            <div>
                {props.posts.map(p => (
                    <Post id={p.id} like={p.likesCount} key={p.id} text={p.text} profile={props.profile}/>
                ))}
            </div>
        </div>
    </div>
    )
}


const MyPostForm = (props) => {

    let onSubmit = (formData) => {
        console.log(formData)
    }

    return <>
        <Form onSubmit={onSubmit}
        >
            {({handleSubmit, pristine, reset, submitting}) => (
                <form onSubmit={handleSubmit}>
                    <Field component='input' name='myPost' type='text' placeholder='Make your the new post...'
                           value={props.newPostText}/>
                    <button><a href='#'>Send</a></button>
                </form>
            )}
        </Form>

    </>

}
















export default MyPosts;
