import {connect} from "react-redux";
import {followingAC, removeAC, setCurrentPageAC, setTotalUsersCountAC, setUsersAC} from "../redux/usersReducer";
import React from "react";
import axios from "axios";
import UsersFunctional from "./UsersFunctional";



class UsersClass extends React.Component {
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?count=${this.props.pageSize}&page=${this.props.currentPage}`)
            .then(response => {
                this.props.setUsers(response.data.items);
                // this.props.setTotalUsersCount(response.data.totalCount);
            })
    }

    onPageChange = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?count=${this.props.pageSize}&page=${this.props.currentPage}`)
            .then(response => {
                this.props.setUsers(response.data.items)
            })
    }

    render = () => {
        return <UsersFunctional totalUsersCount={this.props.totalUsersCount}
                                pageSize={this.props.pageSize}
                                currentPage={this.props.currentPage}
                                onPageChange={this.onPageChange}
                                users={this.props.users}
                                following={this.props.following}
                                remove={this.props.remove}/>
    }
}

let mapStateToProps = (state) => {
    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage
    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        following: (userId) => {dispatch(followingAC(userId))},
        remove: (userId) => {dispatch(removeAC(userId))},
        setUsers: (users) => {dispatch(setUsersAC(users))},
        setCurrentPage: (pageNumber) => {dispatch(setCurrentPageAC(pageNumber))},
        setTotalUsersCount: (totalCount) => {dispatch(setTotalUsersCountAC(totalCount))}
    }
}

const UsersContainer = connect (mapStateToProps, mapDispatchToProps) (UsersClass);

export default UsersContainer;
