import React from "react";
import {addMessageActionCreator, updateNewMessageTextActionText} from "../redux/messagesReducer";
import Messages from "./Messages";


const MessagesContainer = (props) => {
    let state = props.store.getState();

    let sendMessage = () => {
        props.store.dispatch(addMessageActionCreator());
    };

    let onMessageChange = (text) => {
        let action = updateNewMessageTextActionText(text);
        props.store.dispatch(action);
    }

    return (
       <Messages addMessageActionCreator={sendMessage}
                 updateNewMessageTextActionText={onMessageChange}
                 dialogs={state.messagesPage.dialogs}
                 messages={state.messagesPage.messages}
                 newMessageText={state.messagesPage.newMessageText}
       />
    )
}

export default MessagesContainer;
