import axios from "axios";

const getDeleteUser = axios.create({
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    withCredentials: true,
    headers: {
        'API-KEY': '3667b2d2-cd74-4d0f-8711-6a077428126f'
    }
} )

const postUser = axios.create({
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    data: {},
    withCredentials:true,
    headers: {
        'API-KEY': '3667b2d2-cd74-4d0f-8711-6a077428126f'
    }
})


export const UsersApi = {
    getUsers(currentPage = 1, pageSize = 10) {
        return getDeleteUser.get(`users?page=${currentPage}&count=${pageSize}`)
            .then(response => response.data);
    },

    deleteUser(id) {
        return getDeleteUser.delete(`follow/${id}`)
            .then(response =>
                response.data);
    }
}

export const followUserApi = {
    postUser(id) {
        return postUser.post(`follow/${id}`)
            .then(response =>
                response.data);
    }
}
