 import React from "react";
import Profile from "./Profile";
 import * as axios from "axios";
 import {connect} from "react-redux";
 import {profile, profileUser, setUserProfile} from "../../redux/profileReducer";
 import {useLocation, useNavigate, useParams} from "react-router-dom";
 import {UsersApi} from "../../api/api";


class  ProfileClass extends React.Component {
    componentDidMount() {
        let userId = this.props.router.params.userId;
        if(!userId){
            userId = 2;
        }
        this.props.profileUser(userId);
        // axios.get(`https://social-network.samuraijs.com/api/1.0/profile/${userId}`)
        // UsersApi.profile(userId)
        //     .then(data => {
        //         this.props.setUserProfile(data);
        //     })
    }
    render () {
        return (
            <div>
                <Profile {...this.props} profile={this.props.profile} />
            </div>
        )
    }
}

 function withRouter(Component){
    function ComponentWithRouterProps(props){
        let location = useLocation();
        let navigate = useNavigate();
        let params = useParams();
        return(
            <Component {...props} router={{location,navigate,params}} />
        );
    }
    return ComponentWithRouterProps;
 }
 let mapStateToProps = (state) => {
     return {
         profile: state.profilePage.profile
     }
 }
const ProfileContainer = connect (mapStateToProps, {setUserProfile, profileUser}) (withRouter(ProfileClass));
export default ProfileContainer;
