import s from './Messages.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import React from "react";


const Messages = (props) => {
    let dialogsElements = props.stateDialogs.dialogs.map( dialog => (<DialogItem name={dialog.name} id={dialog.id}/>) );

    let messagesElements = props.stateMessages.messages.map( message => (<Message message={message.text} />) );
    let newMessage = React.createRef();
    let sendMessage = () => {
        let text = newMessage.current.value;
        props.addMessage(text);
        newMessage.current.value = '';
    }


    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                { dialogsElements }
            </div>
            <div className={s.messages}>
                { messagesElements }
                <textarea ref={newMessage} placeholder='Введите сообщение'></textarea>
                <button onClick={sendMessage}><a href='#'>Send</a></button>
            </div>
        </div>
    )
}

export default Messages;
