import s from './Messages.module.css';
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {
    let path = '/messages/' + props.id;
    return (
        <div className={s.dialog + ' ' + s.active}>
            <NavLink to={path}>{props.name}</NavLink>
        </div>
    )
}

const Message = (props) => {
    return (
        <div className={s.message}>{props.message}</div>
    )
}

const Messages = (props) => {

    let dialogsData = [ {id: 'david', name: 'David Bowie'},
                        {id: 'queen', name: 'Group Queen'},
                        {id: 'kenye', name: 'Kanye West'}];

    let dialogsElements = dialogsData.map( dialog => (<DialogItem name={dialog.name} id={dialog.id}/>) )


    let messagesData = [ {id: 1, text: 'Pressure pushing down on me'},
                         {id: 2, text:'Pressing down on you, no man ask for'},
                         {id: 3, text:'Um ba ba be'}];

    // <Message message={messagesData[0].message}/>
    // <Message message={messagesData[1].message}/>
    // <Message message={messagesData[2].message}/>

    let messagesElements = messagesData.map( message => (<Message message={message.text} />))

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                { dialogsElements }
            </div>


            <div className={s.messages}>
                { messagesElements }
            </div>
        </div>
    )
}

export default Messages;
