import s from './Users.module.css';
import React from "react";
import axios from "axios";

let Users = (props) => {
    if (props.users.length === 0) {

        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
            props.showMore(response.data.items)
        })
    }



    return <div>
        {
            props.users.map( u => <div key={u.id}>

                <div className={s.user}>

                    <div className={s.userPhoto}>
                            <img src={u.photoUrl}/>
                    </div>

                    <div className={s.userInfo}>
                            <h4 className={s.userFullName}>{u.fullName}</h4>
                            <h5 className={s.userStatus}>{u.status}</h5>
                            <h5 className={s.userLocation}>{u.location.city}, {u.location.country}</h5>
                    </div>

                    <div className={s.userFollow}>
                        { u.followed
                            ? <button onClick={() => {props.following(u.id)}}>Remove</button>
                            : <button onClick={() => {props.remove(u.id)}}>Following</button>
                        }
                    </div>

                </div>
            </div>)
        }
    </div>
}

export default Users;
