import s from './Messages.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import React from "react";
import {addMessageActionCreator, updateNewMessageTextActionText} from "../redux/store";


const Messages = (props) => {

    let state = props.store.getState().messagesPage;

    let dialogsElements = state.dialogs.map( dialog => (<DialogItem name={dialog.name} id={dialog.id}/>) );
    let messagesElements = state.messages.map( message => (<Message message={message.text} />) );

    let sendMessage = () => {
        props.dispatch(addMessageActionCreator());
        props.dispatch(updateNewMessageTextActionText(''));
    };

    let onMessageChange = (e) => {
        let text = e.target.value;
        props.dispatch(updateNewMessageTextActionText(text));
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                <div>{dialogsElements}</div>
            </div>

            <div className={s.messages}>
                <div>{messagesElements}</div>
                <textarea  onChange={onMessageChange} placeholder='Send a message...' value={state.newMessageText}></textarea>
                <button onClick={sendMessage}><a href='#'>Send</a></button>
            </div>
        </div>
    )
}

export default Messages;
