import s from './Users.module.css';
import React from "react";
import userPhoto from "../../img/user.png";


let UsersFunctional = (props) => {

    let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }

    return <div>
        <div>
            { pages.map( p => {
                return <span className={ this.props.currentPage === p && s.selectedPage }
                             onClick={ (e) => {this.onPageChange(p)} } >{p}  </span>
            } ) }
        </div>
        {
            this.props.users.map( u => <div key={u.id}>
                <div className={s.user}>

                    <div className={s.userPhoto}>
                        <img src={ u.photos.small != null ? u.photos.small : userPhoto }/>
                    </div>

                    <div className={s.userInfo}>
                        <h4 className={s.userFullName}>{u.name}</h4>
                        <h5 className={s.userStatus}>{u.status}</h5>
                        <h5 className={s.userLocation}>{'u.location.city'}, {'u.location.country'}</h5>
                    </div>

                    <div className={s.userFollow}>
                        { u.followed
                            ? <button onClick={() => {this.props.following(u.id)}}>Remove</button>
                            : <button onClick={() => {this.props.remove(u.id)}}>Following</button>
                        }
                    </div>

                </div>
            </div>)
        }
    </div>
}

export default UsersFunctional;
