let store = {
    state: {
    profilePage: {
        posts: [
            {id: 'p1', likesCount: 12, text: "Oh, I was not made for heaven. No, I don't want to go to heaven. Hell is much better. Think of all the interesting people you're going to meet down there!"},
            {id: 'p2', likesCount: 3, text: "The reason we're successful, darling? My overall charisma, of course."},
            {id: 'p3', likesCount: 72, text: "I am a romantic, but I do put up a barrier around myself, so it is hard for people to get in and to know the real me."}
        ],
        newPostText: 'Make your the new post...'
    },
    messagesPage: {
        dialogs: [ {id: 'david', name: 'David Bowie'},
            {id: 'queen', name: 'Group Queen'},
            {id: 'kenye', name: 'Kanye West'}],
        messages: [{id: 1, text: 'Pressure pushing down on me'},
            {id: 2, text:'Pressing down on you, no man ask for'},
            {id: 3, text:'Um ba ba be'}]
    }
},

    rerenderEntireTree() {
        console.log('State was changed');
    },

    addPost() {
    let newPost = {
        id: 'p4',
        likesCount: 0,
        text: this.state.profilePage.newPostText
    }
    store.state.profilePage.posts.unshift(newPost);
    this.state.profilePage.newPostText = '';
    this.rerenderEntireTree(this.state);
},

    updateNewPostText(newText) {
    this.state.profilePage.newPostText = newText;
    this.rerenderEntireTree(this.state);
},

    addMessage(messageText) {
    let newMessage = {
        id: 4,
        text: messageText
    }
    this.state.messagesPage.messages.push(newMessage);
    this.rerenderEntireTree(this.state);
},

    subscribe(observer) {
    this.rerenderEntireTree = observer;
},

}

export default store;

window.store = store;
