import React from "react";
import {profileStatus} from "../../../redux/profileReducer";
import {UsersApi} from "../../../api/api";
import {compose} from "redux";
import {connect} from "react-redux";

class ProfileStatus extends React.Component {
    componentDidMount() {
        let userId = this.props.profile.userId;
        this.props.profileStatus(userId);
    }

    state = {
        editMode: false
    }
    activateEditMode = () => {
        this.setState(
            {
                editMode: true
            })
    }
    deactivateEditMode = () => {
        this.setState(
            {
                editMode: false
            })
    }
    onStatusChange = (e) => {
        let status = e.target.value;
        UsersApi.statusNew(status)
            .then(response => {
                this.props.setStatus(response.data);
            });
    }

    render() {
        return (
            <>
                {!this.state.editMode &&
                    <div>
                        <span onDoubleClick={ ()=> {this.activateEditMode()} }>{this.props.status}</span>
                    </div>
                }
                {this.state.editMode &&
                    <div>
                        <input
                            value={this.props.status}
                            autoFocus={true}
                            onBlur={ () => {this.deactivateEditMode()}}
                            onChange={this.onStatusChange}>
                        </input>
                    </div>
                }
            </>
        )
    }
}

let mapStateToProps = (state) => {
    return{
        status: state.profilePage.status
    }
}

export default compose (
    connect(mapStateToProps, {profileStatus}),
)
(ProfileStatus);
